WITH converted AS (
   -- a row looks like this (value, date): 1.45, '2020-12-12'
   SELECT Voltage as BMSVoltage, CAST(strftime('%s', DateTime) as integer) AS datetime FROM BmsReadings
)
SELECT datetime, BMSVoltage FROM converted WHERE datetime BETWEEN ${__from:date:seconds} AND ${__to:date:seconds}

